mapper.select:
SELECT

FROM SysOrderProcess a WHERE 1=1

    and a.accountNo like '%'||#{accountNo}||'%'


    and a.addOneIsValid like '%'||#{addOneIsValid}||'%'


    and a.callOrPutFlag like '%'||#{callOrPutFlag}||'%'


    and a.callOrPutFlag2 like '%'||#{callOrPutFlag2}||'%'


    and a.clientID like '%'||#{clientID}||'%'


    and a.clientIP like '%'||#{clientIP}||'%'


    and a.clientLocalIP like '%'||#{clientLocalIP}||'%'


    and a.clientMac like '%'||#{clientMac}||'%'


    and a.clientOrderNo like '%'||#{clientOrderNo}||'%'


    and a.commodityNo like '%'||#{commodityNo}||'%'


    and a.commodityType like '%'||#{commodityType}||'%'


    and a.contractNo like '%'||#{contractNo}||'%'


    and a.contractNo2 like '%'||#{contractNo2}||'%'


    and a.errorCode like '%'||#{errorCode}||'%'


    and a.errorText like '%'||#{errorText}||'%'


    and a.exchangeNo like '%'||#{exchangeNo}||'%'


    and a.expireTime = #{expireTime}


    and a.hedgeFlag like '%'||#{hedgeFlag}||'%'


    and a.inquiryNo like '%'||#{inquiryNo}||'%'


    and a.isAddOne like '%'||#{isAddOne}||'%'


    and a.isBackInput like '%'||#{isBackInput}||'%'


    and a.isDeleted like '%'||#{isDeleted}||'%'


    and a.isRiskOrder like '%'||#{isRiskOrder}||'%'


    and a.licenseNo like '%'||#{licenseNo}||'%'


    and a.maxClipSize = #{maxClipSize}


    and a.minClipSize = #{minClipSize}


    and a.orderCommandUserNo like '%'||#{orderCommandUserNo}||'%'


    and a.orderExchangeSystemNo like '%'||#{orderExchangeSystemNo}||'%'


    and a.orderInsertTime = #{orderInsertTime}


    and a.orderInsertUserNo like '%'||#{orderInsertUserNo}||'%'


    and a.orderLocalNo like '%'||#{orderLocalNo}||'%'


    and a.orderMatchPrice = #{orderMatchPrice}


    and a.orderMatchPrice2 = #{orderMatchPrice2}


    and a.orderMatchQty = #{orderMatchQty}


    and a.orderMatchQty2 = #{orderMatchQty2}


    and a.orderMinQty = #{orderMinQty}


    and a.orderNo like '%'||#{orderNo}||'%'


    and a.orderParentSystemNo like '%'||#{orderParentSystemNo}||'%'


    and a.orderPrice = #{orderPrice}


    and a.orderPrice2 = #{orderPrice2}


    and a.orderQty = #{orderQty}


    and a.orderSide like '%'||#{orderSide}||'%'


    and a.orderSource like '%'||#{orderSource}||'%'


    and a.orderState like '%'||#{orderState}||'%'


    and a.orderStreamID = #{orderStreamID}


    and a.orderSystemNo like '%'||#{orderSystemNo}||'%'


    and a.orderType like '%'||#{orderType}||'%'


    and a.orderUpdateTime = #{orderUpdateTime}


    and a.orderUpdateUserNo like '%'||#{orderUpdateUserNo}||'%'


    and a.positionEffect like '%'||#{positionEffect}||'%'


    and a.positionEffect2 like '%'||#{positionEffect2}||'%'


    and a.refDouble = #{refDouble}


    and a.refInt = #{refInt}


    and a.refString like '%'||#{refString}||'%'


    and a.serverFlag like '%'||#{serverFlag}||'%'


    and a.stopPrice = #{stopPrice}


    and a.strikePrice = #{strikePrice}


    and a.strikePrice2 = #{strikePrice2}


    and a.tacticsType like '%'||#{tacticsType}||'%'


    and a.timeInForce like '%'||#{timeInForce}||'%'


    and a.triggerCondition like '%'||#{triggerCondition}||'%'


    and a.triggerPriceType like '%'||#{triggerPriceType}||'%'


    and a.upperChannelNo like '%'||#{upperChannelNo}||'%'


    and a.upperNo like '%'||#{upperNo}||'%'


    and a.upperStreamID = #{upperStreamID}


    and a.remark like '%'||#{remark}||'%'


    and a.createDate = #{createDate}


    and a.updateDate = #{updateDate}
